// Here, we explore what can cause a t-bit exception.
// We first create a new function called void (*fun_ptr)(void)
// and set the address to the address of change_access_level_unpriv.
// Then, we run and see that the address of the fun_ptr is odd rather
// than even (change_access_level_unpriv). If we go to the .list file,
// we can hardcode the even address and set the new function equal to that.
// This causes an exception!
// 
// Typically, the compiler will take care of making the t-bit odd for you.
// Hardcoding, however, has caused the exception, and makes us to go to the
// hardFault_handler function. Thus, we learn that not maintaining the
// T=bit as '1' will cause an exception (attempt to switch to invalid state).

#include <stdint.h>
#include <stdio.h>

/* Function Prototype(s) */
extern void initialise_monitor_handles(void) ;									        // Debugger
void generate_interrupt(void) ;													        // Executes in THREAD MODE of the processor
void RTC_WKUP_IRQHandler(void) ;
void change_access_level_unpriv(void) ;
void hardFault_handler(void) ;

/* This function executes in the THREAD MODE + PRIV ACCESS LEVEL of the processor */
int main(void)
{ 
	initialise_monitor_handles() ;												        // Debugger

	printf("In thread mode: before interrupt\n") ;

    void (*fun_ptr)(void) ;
    
#if 0
    fun_ptr = change_access_level_unpriv ;
#else
    fun_ptr = (void*) (0x080001fc) ;                                                    // Address of change_access_level_unpriv found in .list file
#endif


	fun_ptr() ;
	
	generate_interrupt() ;

	printf("In thread mode: after interrupt\n") ;

    /* Loop forever */
	for(;;);
}

/* This function executes in the THREAD MODE of the processor, and can only be accessed with privileged access level */
void generate_interrupt(void) {
	uint32_t	*pSTIR	= (uint32_t*) 0xE000EF00 ;
	uint32_t	*pISER0	= (uint32_t*) 0xE000E100 ;

	// Enable IRQ3 interrupt
	*pISER0 |= (1 << 3) ;

	// Generate an interrupt from software for IRQ3
	*pSTIR = (3 & 0x1FF) ;
}

void RTC_WKUP_IRQHandler(void) {
	printf("In handler mode: ISR\n") ;
}

void change_access_level_unpriv(void) {
	// Read		(Move special register to general purpose register)
	__asm volatile ("MRS R0, CONTROL") ;
	// Modify	(Or/Set 0th bit)
	__asm volatile ("ORR R0, R0, #0x01") ;                                              // Look at CONTROL register and set nPRIV bit to 1
	// Write	(Write back to CONTROL using general purpose to special register)
	__asm volatile ("MSR CONTROL, R0") ;

}

void hardFault_handler(void) {
	printf("Hard fault detected\n") ;
	while (1) ;
}
